key90009009={90009009,99999,2,"关卡未开启",1,1,30,0,0,1,4,6,0,"关卡未开启",654,100,792,"","1,1","关卡未开启"},
key10001001={10001001,20001,1,"凤出琅琊",1,1,0,0,2,3,4,6,1231,"梅长苏踏下琅琊山，准备金陵之行，一了夙愿。",653,1,902,"1009001,1;1009002,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,360","推荐英雄等级：1~2\n普通的敌人。"},
key10001002={10001002,20001,2,"歇雨崖边",1,1,0,10001001,2,3,4,6,1232,"半山歇雨崖，遇见来迎接的金陵二少，言豫津和萧景睿。",656,1,423,"1018001,10;1014001,10;1012001,5;1,50","1,10;2,360","推荐英雄等级：1~2\n普通的敌人。"},
key10001003={10001003,20001,3,"叹海楼前",2,1,0,10001002,2,3,4,6,1233,"三人同行，途径叹海楼，见到被峭龙帮追杀的卓青遥。",1580,1,1129,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,460","推荐英雄等级：2~3\n精英，擅于内功攻击最前排目标。"},
key10001004={10001004,20001,4,"擎天一束",3,1,0,10001003,2,3,4,6,1234,"峭龙帮帮主束擎天被迫露面，江湖人也并非自在高远。",658,1,415,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,540","推荐英雄等级：2~3\n精英，擅于内功攻击竖列目标。"},
key10002001={10002001,20002,1,"金陵偶遇",1,1,0,10001004,1,2,4,6,1221,"苏哲三人入金陵，城门遇霓凰，风起车帘，却见故人飒爽风姿尤胜当年。",657,3,798,"1012001,1;2,10000;1010001,10;1014001,10;1012001,5;1,50","1,10;2,360","推荐英雄等级：4~5\n普通的敌人。"},
key10002002={10002002,20002,2,"比武招亲",1,1,0,10002001,1,2,4,6,1222,"霓凰掌军得权呼声高，帝王忌惮，借其年龄渐长为由举办比武招亲。",653,3,435,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,540","推荐英雄等级：4~5\n普通的敌人。"},
key10002003={10002003,20002,3,"风云渐起",2,1,0,10002002,1,2,4,6,1223,"得霓凰者即得虎符，兵权诱惑，报名者涉及各国势力，搅起金陵暗涌。",1580,3,1489,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,600","推荐英雄等级：7~8\n精英，擅于内功攻击全体目标。"},
key10002004={10002004,20002,4,"龙蛇混杂",3,1,0,10002003,1,2,4,6,1224,"比武招亲参赛者良莠不齐，豫津景睿报名参赛，意为穆姐姐淘汰鱼目。",658,3,1725,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,640","推荐英雄等级：8~9\nBOSS，擅于外功攻击单体目标。"},
key10002005={10002005,20003,1,"麒麟才子",1,1,0,10002004,0,1,4,6,1231,"誉王一掷千金得锦囊：“麒麟才子，江左梅郎，得之，可得天下”",654,5,1114,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,820","推荐英雄等级：10~11\n普通的敌人。"},
key10002006={10002006,20003,2,"东宫太子",1,1,0,10002005,0,1,4,6,1232,"太子得消息，知麒麟之才，愤怒誉王狼子野心，决意招揽梅长苏。",657,5,421,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,720","推荐英雄等级：10~11\n普通的敌人。"},
key10002007={10002007,20003,3,"争相招揽",2,1,0,10002006,0,1,4,6,1233,"太子誉王迎风楼“偶遇”苏哲争相讨好，苏哲好意均受然并不答复。",1580,5,756,"1012001,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,760","推荐英雄等级：10~11\n精英，擅于内功攻击全体目标。"},
key10002008={10002008,20003,4,"双龙夺嫡",3,1,0,10002007,0,1,4,6,1234,"麒麟才子消息不胫而走，龙子夺嫡由暗转明，自此，梅长苏卷入金陵风波。",658,5,1489,"2,8000;2,1000;1014001,10;1012001,5;1,50","1,10;2,780","推荐英雄等级：10~11\nBOSS，擅于内功攻击最前排目标。"},
key10001011={10001011,10001,1,"太皇太后",1,1,0,10002008,0,1,4,6,1121,"太皇太后约见晚辈，人老心明，识得林殊，叠握霓凰小殊双手祝福。",657,10,1110,"1014001,10;2,1000;1012001,5;1,50","1,10;2,780","推荐英雄等级：11~12\n普通的敌人。"},
key10001013={10001013,10001,2,"霓凰相约",1,1,0,10001011,0,1,4,6,1122,"林殊不舍太皇太后，眼中泄露真情意，霓凰起疑，约“苏先生”相谈。",655,10,798,"2,20000;1014001,10;1012001,5;1,50","1,10;2,820","推荐英雄等级：11~12\n普通的敌人。"},
key10001014={10001014,10001,3,"何为有缘人",2,1,0,10001013,0,1,4,6,1123,"霓凰试探，询问苏哲何为有缘人，直视之下苏哲苦笑怔住。",1580,10,1488,"1014001,10;1012001,5;1,50","1,10;2,820","推荐英雄等级：12~13\n精英，对全体目标施加持续毒伤害。"},
key10001012={10001012,10001,4,"有缘自在心",3,1,0,10001014,0,1,4,6,1124,"苏哲怕泄漏情绪，不敢直视霓凰，答曰缘分自在郡主心中。",658,10,798,"1014001,10;2,1000;1012001,5;1,50","1,10;2,840","推荐英雄等级：12~13\nBOSS，擅于外功攻击单体目标。"},
key10001021={10001021,10002,1,"择主何人",1,1,0,10001012,0,1,4,6,1231,"霓凰放下苏哲是故人的怀疑，询问其欲择主何人。",652,12,902,"1014001,10;2,1000;1012001,5;1,110","1,10;2,840","推荐英雄等级：15~16\n普通的敌人。"},
key10001022={10001022,10002,2,"罪奴小庭生",1,1,0,10001021,0,1,4,6,1232,"苏哲并未给出确切答复，两人行至后庭，遇太监鞭笞掖庭罪奴庭生。",653,12,417,"1014001,10;2,1000;1012001,5;1,110","1,10;2,840","推荐英雄等级：15~16\n普通的敌人。"},
key10001023={10001023,10002,3,"靖王与庭生",2,1,0,10001022,0,1,4,6,1233,"太监言语之中透露靖王竟十分照佛小庭生，苏哲起疑与霓凰保护庭生。",1580,12,792,"1014001,10;2,1000;1012001,5;1,110","1,10;2,860","推荐英雄等级：16~17\n精英，擅于外功攻击单体目标。"},
key10001024={10001024,10002,4,"许诺救庭生",3,1,0,10001023,0,1,4,6,1234,"靖王赶来，欲救庭生然力所难及，苏哲沉思，许下诺言有办法救出庭生。",658,12,793,"1014001,10;1012001,5;1,110","1,10;2,860","推荐英雄等级：16~17\nBOSS，擅于内功攻击竖列目标。"},
key10001031={10001031,10003,1,"豫津参赛",1,1,0,10001024,0,1,4,6,1221,"比武招亲继续进行，言豫津将要参赛，兴奋之下力邀众人观战。",654,16,430,"1014001,10;1012001,5;1,110","1,10;2,860","推荐英雄等级：17~18\n普通的敌人。"},
key10001032={10001032,10003,2,"正当年少",1,1,0,10001031,0,1,4,6,1222,"第一场比试豫津轻松过关，年轻人的朝气蓬勃令苏哲驻足。",653,16,902,"1014001,10;1012001,5;1,110","1,10;2,980","推荐英雄等级：17~18\n普通的敌人。"},
key10001034={10001034,10003,3,"高手夏无眠",2,1,0,10001032,0,1,4,6,1223,"得知新对手是江湖高手夏无眠，一手剑法细密强劲，豫津劲头十足。",1580,16,420,"1014001,10;1012001,5;1,110","1,10;2,1000","推荐英雄等级：18~19\n精英，擅于内功攻击全体敌人。"},
key10001033={10001033,10003,4,"落英缤纷",3,1,0,10001034,0,1,4,6,1224,"个性张扬的豫津一招“落英缤纷”舞得十分精彩，漂亮赢下一局。",658,16,481,"1014001,10;1012001,5;1,110","1,10;2,1000","推荐英雄等级：19~20\nBOSS，擅于内功攻击全体敌人。"},
key10001041={10001041,10004,1,"北燕百里奇",1,1,0,10001033,0,1,4,6,1121,"比武场现奇人，北燕“百里奇”一招打败青年才俊秦尚志引骚动。",652,19,1129,"1014001,10;1012001,5;1,110","1,10;2,1000","推荐英雄等级：20~21\n普通的敌人。"},
key10001043={10001043,10004,2,"霓凰相约",1,1,0,10001041,0,1,4,6,1122,"梁帝担忧赛果，霓凰潜意识只有梅长苏有办法解决百里奇，相约见面。",658,19,902,"1014001,10;1012001,5;1,110","1,10;2,1000","推荐英雄等级：20~21\n普通的敌人。"},
key10001044={10001044,10004,3,"幼子打法",2,1,0,10001043,0,1,4,6,1123,"梅长苏谈及百里奇，称打败其并非不可，提出可教幼子阵法将其战胜。",1580,19,438,"1014001,10;1012001,5;1,110","1,10;2,1020","推荐英雄等级：21~22\n精英，擅于内功攻击全体敌人。"},
key10001046={10001046,10004,4,"霓凰相助",3,1,0,10001044,0,1,4,6,1124,"霓凰隐约猜到梅长苏欲借此事救出庭生，便向梁帝提出此方法。",658,19,1487,"1014001,10;1012001,5;1,110","1,10;2,1020","推荐英雄等级：21~22\nBOSS，擅于内功攻击最前排目标。"},
key10001051={10001051,10005,1,"夏无眠不忿",1,1,0,10001046,0,1,4,6,1231,"夏无眠被言豫津打败，并且输得难看，心生不忿，欲夺回声誉。",657,21,420,"1014001,10;1012001,5;1,110","1,10;2,1020","推荐英雄等级：22~23\n普通的敌人。"},
key10001053={10001053,10005,2,"兄弟夏生愁",1,1,0,10001051,0,1,4,6,1232,"兄弟同心，兄长夏生愁为弟报仇，参赛比武招亲。",658,21,481,"1014001,10;1012001,5;1,110","1,10;2,1020","推荐英雄等级：22~23\n普通的敌人。"},
key10001054={10001054,10005,3,"毒辣伤人",2,1,0,10001053,0,1,4,6,1233,"夏生愁手法毒辣，意不在胜，却在伤人，引起公愤。",1580,21,481,"1014001,10;1012001,5;1,110","1,10;2,1040","推荐英雄等级：23~24\n精英，擅于内功攻击竖列目标。"},
key10001056={10001056,10005,4,"心服口服",3,1,0,10001054,0,1,4,6,1234,"梅长苏提议出奇制胜，终是打败夏生愁，令其心服口服。",658,21,420,"1014001,10;1012001,5;1,110","1,10;2,1040","推荐英雄等级：23~24\nBOSS，擅于内功攻击竖列目标。"},
key10001061={10001061,10006,1,"庭生入选",1,1,0,10001056,0,1,4,6,1221,"梁帝同意幼子方案，将掖庭幼子带至大殿供选，梅长苏借机择选庭生。",655,23,417,"1014001,10;1012001,5;1,110","1,10;2,1040","推荐英雄等级：24~25\n普通的敌人。"},
key10001063={10001063,10006,2,"速成阵法",1,1,0,10001061,0,1,4,6,1222,"梅长苏令飞流教导小儿速成阵法，颇有小成，大将军蒙挚赞不绝口。",653,23,69,"1014001,10;1012001,5;1,110","1,10;2,1040","推荐英雄等级：24~25\n普通的敌人。"},
key10001064={10001064,10006,3,"一决胜负",2,1,0,10001063,0,1,4,6,1223,"终于到了决一胜负的时刻，小儿们不负众望，果然打败百里奇。",1580,23,1129,"1014001,10;1012001,5;1,110","1,10;2,1060","推荐英雄等级：25~26\n精英，擅于内功攻击最前排目标。"},
key10001066={10001066,10006,4,"庭生获救",3,1,0,10001064,0,1,4,6,1224,"梁帝大喜，梅长苏霓凰趁机提出奖励罪奴，梁帝终松口放出庭生。",658,23,1489,"1014001,10;1012001,5;1,110","1,10;2,1060","推荐英雄等级：25~26\nBOSS，擅于内功攻击竖列目标。"},
key10001071={10001071,10007,1,"名声大噪",1,1,0,10001066,0,1,4,6,1231,"梅长苏名声大噪，太子誉王再是按耐不住，争相招揽。",657,24,902,"1014001,10;1012001,5;1,110","1,10;2,1060","推荐英雄等级：25~26\n普通的敌人。"},
key10001073={10001073,10007,2,"投誉王门下",1,1,0,10001071,0,1,4,6,1232,"梅长苏假意投誉王门下，太子怒，求之不得便欲毁之杀之。",652,24,421,"1014001,10;1012001,5;1,110","1,10;2,1060","推荐英雄等级：25~26\n普通的敌人。"},
key10001074={10001074,10007,3,"夜黑风高",2,1,0,10001073,0,1,4,6,1233,"梅长苏回雪炉，夜黑风高杀人夜，神秘杀手诡异出现，欲夺其性命。",1580,24,775,"1014001,10;1012001,5;1,110","1,10;2,1060","推荐英雄等级：26~27\n精英，擅于内功攻击单体目标。"},
key10001076={10001076,10007,4,"黑衣杀手",3,1,0,10001074,0,1,4,6,1234,"飞流奋力抵抗杀手，然而敌众我寡，梅长苏遇危，形势并不乐观。",658,24,775,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：26~27\nBOSS，擅于内功攻击最前排目标。"},
key10001081={10001081,10008,1,"飞流的武功",1,1,0,10001076,0,1,4,6,1221,"其行如风，飞流身形诡异，捉摸不透，杀手终不敌倒下。",652,25,398,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：26~27\n普通的敌人。"},
key10001083={10001083,10008,2,"谢玉府兵",1,1,0,10001081,0,1,4,6,1222,"细丝小结是真相，梅长苏找出杀手线索，竟是谢玉府兵，不禁微笑。",658,25,424,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：26~27\n普通的敌人。"},
key10001084={10001084,10008,3,"心如明镜",2,1,0,10001083,0,1,4,6,1223,"心中明镜，谢玉，你终于露出狐狸尾巴，这次刺杀也会有完美解释吧。",1580,25,71,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：27~28\n精英，擅于外功突击后排单体目标。"},
key10001086={10001086,10008,4,"切磋比试",3,1,0,10001084,0,1,4,6,1224,"二人回谢府，谢玉见二人回来不露惊诧，只解释为府兵与飞流比试。",658,25,71,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：27~28\nBOSS，擅于外功突击后排单体目标。"},
key10001091={10001091,10009,1,"心如深海",1,1,0,10001086,0,1,4,6,8101,"梅长苏淡笑不语，飞流冷眼相对，眼望两人伫立门前，谢玉心下纷乱。",657,26,424,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：27~28\n普通的敌人。"},
key11001092={11001092,10009,2,"雪炉来客",1,1,0,10001091,0,1,4,6,8102,"二人回到雪炉，飞流呛声询问“什么人？”声起影随，人已攻出。",652,26,398,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：27~28\n普通的敌人。"},
key10001093={10001093,10009,3,"剑影纠缠",1,1,0,11001092,0,1,4,6,8103,"眨眼间，隐秘处飞出四五个黑影，均与飞流剑影纠缠。",653,26,1782,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：27~28\n普通的敌人。"},
key11001094={11001094,10009,4,"来者何人",2,1,0,10001093,0,1,4,6,8104,"梅长苏驻足观望，来人不算多，手法并不狠辣，招招仅为自卫。",1580,26,1490,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：27~28\n精英，擅于内功攻击单体目标。"},
key10001095={10001095,10009,5,"心有答案",1,1,0,11001094,0,1,4,6,8105,"心中有答案，梅长苏喊住飞流“飞流不要打了”，飞流听话停下。",654,26,398,"1014001,10;1012001,5;1,110","1,10;2,1080","推荐英雄等级：28~29\n普通的敌人。"},
key11001097={11001097,10009,6,"莅阳长公主",1,1,0,10001095,0,1,4,6,8106,"望向密林，梅长苏微笑“深夜来访必有急事，长公主不如进来说话。”",655,26,902,"1014001,10;1012001,5;1,110","1,10;2,1100","推荐英雄等级：28~29\n普通的敌人。"},
key11001098={11001098,10009,7,"有事相求",1,1,0,11001097,0,1,4,6,8107,"果不其然，密林徐徐走出一人，真是谢玉之妻，一脸焦急的莅阳。",656,26,400,"1014001,10;1012001,5;1,110","1,10;2,1100","推荐英雄等级：28~29\n普通的敌人。"},
key10001096={10001096,10009,8,"情丝绕",3,1,0,11001098,0,1,4,6,8108,"莅阳告知宫中又现“情丝绕”，应是针对霓凰，请求梅长苏相助。",658,26,1490,"1014001,10;1012001,5;1,110","1,10;2,1100","推荐英雄等级：28~29\nBOSS，擅于内功攻击竖列目标。"},
key10001101={10001101,10010,1,"心思百转",1,1,0,10001096,0,1,4,6,8201,"得知何为“情丝绕”，心思急转猜到宫中有人欲用肮脏手段陷害霓凰。",654,28,902,"1014001,10;1012001,5;1,110","1,10;2,1100","推荐英雄等级：29~30\n普通的敌人。"},
key10001102={10001102,10010,2,"急血攻心",1,1,0,10001101,0,1,4,6,8202,"梅长苏心下焦虑，呕出一口攻心急血，飞流担忧不已，急寻蒙挚。",655,28,69,"1014001,10;1012001,5;1,110","1,10;2,1100","推荐英雄等级：29~30\n普通的敌人。"},
key10001103={10001103,10010,3,"金陵巨盗",1,1,0,10001102,0,1,4,6,8203,"然而蒙挚并不在营中，被告知正带弟兄去缉拿金陵巨盗唐氏姐妹。",652,28,797,"1014001,10;1012001,5;1,110","1,10;2,1120","推荐英雄等级：29~30\n普通的敌人。"},
key11001104={11001104,10010,4,"唐氏姐妹",2,1,0,10001103,0,1,4,6,8204,"唐氏姐妹。身手矫健，一式轻功形如鬼魅，多盗大户官宦之家。",1580,28,1782,"1014001,10;1012001,5;1,110","1,10;2,1120","推荐英雄等级：29~30\n精英，擅于对目标施加持续毒伤害。"},
key10001105={10001105,10010,5,"巨盗动向",1,1,0,11001104,0,1,4,6,8205,"梅长苏根据线索，猜测出唐氏姐妹动向，让飞流速找人通知蒙挚。",653,28,69,"1014001,10;1012001,5;1,110","1,10;2,1120","推荐英雄等级：30~31\n普通的敌人。"},
key11001106={11001106,10010,6,"巨富金家",1,1,0,10001105,0,1,4,6,8206,"寻到蒙挚，梅长苏将想法提出，唐氏姐妹的目标应是巨富金家。",656,28,1488,"1014001,10;1012001,5;1,110","1,10;2,1120","推荐英雄等级：30~31\n普通的敌人。"},
key11001108={11001108,10010,7,"天罗地网",1,1,0,11001106,0,1,4,6,8207,"蒙挚按照梅长苏提供的线索，布下天罗地网，果然擒住两姐妹。",657,28,797,"1014001,10;1012001,5;1,110","1,10;2,1120","推荐英雄等级：30~31\n普通的敌人。"},
key10001107={10001107,10010,8,"蒙挚会和",3,1,0,11001108,0,1,4,6,8208,"金陵巨盗案终于告破，蒙挚速速脱身与梅长苏会和。",658,28,1782,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：30~31\nBOSS，擅于对目标施加持续毒伤害。"},
key10001111={10001111,10011,1,"小心皇后",1,1,0,10001107,0,1,4,6,8301,"蒙挚寻到梅长苏，得知梅长苏已提醒进宫的霓凰，小心皇后。",653,31,69,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：31~32\n普通的敌人。"},
key10001112={10001112,10011,2,"幕后主使",1,1,0,10001111,0,1,4,6,8302,"梅长苏猜测，召霓凰进宫的后宫之人，应就是情丝绕的幕后主使。",652,31,73,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：31~32\n普通的敌人。"},
key10001113={10001113,10011,3,"猜错了",1,1,0,10001112,0,1,4,6,8303,"却得知誉王今日并未入宫，梅长苏猛然发觉设计霓凰的凶手不是皇后。",656,31,1114,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：31~32\n普通的敌人。"},
key11001114={11001114,10011,4,"晚了一步",2,1,0,10001113,0,1,4,6,8304,"蒙挚急急入宫寻霓凰，然而还是晚了一步，霓凰已被越贵妃召走。",1580,31,1782,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：31~32\n精英，擅于内功攻击单体目标。"},
key10001115={10001115,10011,5,"打听消息",1,1,0,11001114,0,1,4,6,8305,"武将无法入后宫，蒙挚无可奈何，只得派出心腹继续打听。",655,31,69,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：32~33\n普通的敌人。"},
key11001116={11001116,10011,6,"霓凰有危",1,1,0,10001115,0,1,4,6,8306,"蒙挚将宫中消息告知梅长苏，两人都明白，霓凰必定十分危险。",654,31,902,"1014001,10;1012001,5;1,110","1,10;2,1140","推荐英雄等级：32~33\n普通的敌人。"},
key11001118={11001118,10011,7,"四管齐下",1,1,0,11001116,0,1,4,6,8307,"梅长苏想出无可奈何之法，需要靖王、誉王、皇后、穆青四管齐下。",657,31,66,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：32~33\n普通的敌人。"},
key10001117={10001117,10011,8,"求人相助",3,1,0,11001118,0,1,4,6,8308,"当下蒙挚去寻皇上，梅长苏求得靖王提前帮手，并告知穆青守宫禁。",658,31,774,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：32~33\nBOSS，擅于对目标施加持续毒伤害。"},
key10001121={10001121,10012,1,"静心求法",1,1,0,10001117,0,1,4,6,8401,"靖王在宫中并没有势力，梅长苏知道他应对不了太久，心下急转。",657,33,902,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：33~34\n普通的敌人。"},
key10001122={10001122,10012,2,"低头求助",1,1,0,10001121,0,1,4,6,8402,"要救霓凰，蒙挚虽可帮手，却不可出面，梅长苏知道只能向誉王低头。",656,33,1114,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：33~34\n普通的敌人。"},
key10001123={10001123,10012,3,"誉王府",1,1,0,10001122,0,1,4,6,8403,"太子得霓凰，最受损害的即是誉王，梅长苏苦笑，只身前往誉王府。",655,33,902,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：33~34\n普通的敌人。"},
key11001124={11001124,10012,4,"小鬼最难缠",2,1,0,10001123,0,1,4,6,8404,"俗话说得好，阎王好说话，小鬼最难缠，没见着誉王，却……",1580,33,1725,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：33~34\n精英，擅于内功攻击竖列目标。"},
key10001125={10001125,10012,5,"狗腿灰鹞",1,1,0,11001124,0,1,4,6,8405,"得知来人是梅长苏，誉王府家卫，狗腿灰鹞嘲当下冷脸嘲讽。",654,33,1726,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：34~35\n普通的敌人。"},
key11001126={11001126,10012,6,"求见誉王",1,1,0,10001125,0,1,4,6,8406,"梅长苏面色未动，按住飞流，无视灰鹞继续求见誉王。",653,33,902,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：34~35\n普通的敌人。"},
key11001128={11001128,10012,7,"强闯誉王府",1,1,0,11001126,0,1,4,6,8407,"灰鹞摆起谱来，梅长苏无奈命飞流出手，欲强行闯入。",652,33,398,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：34~35\n普通的敌人。"},
key10001127={10001127,10012,8,"落花流水",3,1,0,11001128,0,1,4,6,8408,"灰鹞哪里是飞流的对手，几招下来被打得落花流水，二人强行入府。",658,33,1726,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：34~35\nBOSS，擅于外功攻击最前排目标。"},
key10001131={10001131,10013,1,"款款而来",1,1,0,10001127,0,1,4,6,8101,"灰鹞落败，二人正要入府，却见一眼角娇媚的女子款款而来。",656,34,413,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：35~36\n普通的敌人。"},
key10001132={10001132,10013,2,"秦般弱",1,1,0,10001131,0,1,4,6,8102,"来人原是誉王谋士秦般弱，梅长苏微眯双眼，知道不能小瞧这个女子。",655,34,413,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：35~36\n普通的敌人。"},
key10001133={10001133,10013,3,"探知实力",1,1,0,10001132,0,1,4,6,8103,"秦般弱并未无礼，言语试探梅长苏，欲探出其真实实力。",652,34,413,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：35~36\n普通的敌人。"},
key11001134={11001134,10013,4,"不耐纠缠",2,1,0,10001133,0,1,4,6,8104,"梅长苏担忧霓凰，急见誉王，不耐与其言辞纠缠，秦般弱变脸。",1580,34,413,"1014001,10;1012001,5;1,110","1,10;2,1160","推荐英雄等级：35~36\n精英，擅于对目标施加持续毒伤害。"},
key10001135={10001135,10013,5,"大打出手",1,1,0,11001134,0,1,4,6,8105,"飞流早已不耐烦，当下剑已出鞘，和秦般弱的护卫打在一起。",653,34,398,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：36~37\n普通的敌人。"},
key11001136={11001136,10013,6,"残兵败将",1,1,0,10001135,0,1,4,6,8106,"飞流出手并无轻重之分，将一干人等迅速打败，残兵败将丢了一地。",654,34,398,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：36~37\n普通的敌人。"},
key11001138={11001138,10013,7,"你们无礼",1,1,0,11001136,0,1,4,6,8107,"誉王闻声终于姗姗来迟，见到梅长苏，急忙黑脸斥责手下无礼。",657,34,1114,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：36~37\n普通的敌人。"},
key10001137={10001137,10013,8,"誉王入宫",3,1,0,11001138,0,1,4,6,8108,"听闻太子算计霓凰，誉王暗道天助我也，答应梅长苏入宫相帮。",658,34,413,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：36~37\nBOSS，擅于对目标施加持续毒伤害。"},
key10001141={10001141,10014,1,"有人欢喜",1,1,0,10001137,0,1,4,6,8201,"誉王得知消息后，风风火火入宫寻到皇后，喜滋滋的简述了霓凰遇险。",653,36,1114,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：37~38\n普通的敌人。"},
key10001142={10001142,10014,2,"机不可失",1,1,0,10001141,0,1,4,6,8202,"皇后并非愚钝之人，瞬间明白了个中奥妙，暗道机不可失失不再来。",654,36,1114,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：37~38\n普通的敌人。"},
key10001143={10001143,10014,3,"失不再来",1,1,0,10001142,0,1,4,6,8203,"宫中秘辛，一旦戳破那层窗户纸，越贵妃和太子此次必会倒下一个。",657,36,435,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：37~38\n普通的敌人。"},
key11001144={11001144,10014,4,"直闯东宫",2,1,0,10001143,0,1,4,6,8204,"皇后誉王心喜之下直入东宫，心道梅长苏果然是麒麟才子，福将啊。",1580,36,774,"1014001,10;1012001,5;1,110","1,10;2,1180","推荐英雄等级：37~38\n精英，擅于外功攻击单体目标。"},
key10001145={10001145,10014,5,"饮下情丝绕",1,1,0,11001144,0,1,4,6,8205,"霓凰已被越贵妃哄骗饮下情丝绕，身软无力眼不视物，当即怒火攻心。",656,36,798,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：38~39\n普通的敌人。"},
key11001146={11001146,10014,6,"司马雷",1,1,0,10001145,0,1,4,6,8206,"越贵妃召来心腹司马雷，欲乘机对霓凰行下不轨之事。",655,36,1481,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：38~39\n普通的敌人。"},
key11001148={11001148,10014,7,"武将之威",1,1,0,11001146,0,1,4,6,8207,"穆霓凰久经沙场，威势仍在，急怒之下推开司马雷欲离东宫。",652,36,798,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：38~39\n普通的敌人。"},
key10001147={10001147,10014,8,"靖王赶到",3,1,0,11001148,0,1,4,6,8208,"靖王得消息火速赶往东宫，却见霓凰倒地，越贵妃怂恿司马雷欲行不轨。",658,36,1109,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：38~39\nBOSS，擅于内功攻击全体目标。"},
key10001151={10001151,10015,1,"气场骇人",1,1,0,10001147,0,1,4,6,8401,"军队历练下拥有的不仅是功夫，更有骇人气场，司马雷趔趄。",652,38,774,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key10001152={10001152,10015,2,"救起霓凰",1,1,0,10001151,0,1,4,6,8402,"靖王扶起霓凰，不令人靠近，越贵妃对这半路杀出的程咬金又怕又恨。",654,38,66,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key10001153={10001153,10015,3,"起杀心",1,1,0,10001152,0,1,4,6,8403,"宫内之事若是泄漏，太子就毁了。死人才不会乱说话，越贵妃起杀心。",653,38,435,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key11001154={11001154,10015,4,"弓箭手",2,1,0,10001153,0,1,4,6,8404,"越贵妃情急之下命弓箭手围靖王，想要杀人灭口死无对证。",1580,38,1488,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\nBOSS，擅于内功攻击全体目标"},
key10001155={10001155,10015,5,"挟持太子",1,1,0,11001154,0,1,4,6,8405,"对这对残害忠良将士的母子，靖王心生忿恨，飞身挟持太子。",655,38,421,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key11001156={11001156,10015,6,"誉王现身",1,1,0,10001155,0,1,4,6,8406,"誉王几人冲入东宫，眼见便是密密麻麻的弓箭手直指靖王与太子。",656,38,1114,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key11001158={11001158,10015,7,"杀无赦",1,1,0,11001156,0,1,4,6,8407,"越贵妃心急之下歪曲事实，称靖王狼子野心挟持太子，应当杀无赦。",657,38,66,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key10001157={10001157,10015,8,"陛下定夺",3,1,0,11001158,0,1,4,6,8408,"皇后怎会放过如此机会掰倒太子，当机立断表忠心：一切等皇上定夺。",658,38,421,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：39~40\nBOSS，擅于内功攻击全体目标。"},
key10001161={10001161,10016,1,"帝王心",1,1,0,10001157,0,1,4,6,8301,"自此，霓凰和靖王算是安全了，然而真相只靠一张嘴，谁又懂帝王心。",656,39,798,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：40~41\n普通的敌人。"},
key10001162={10001162,10016,2,"一个交代",1,1,0,10001161,0,1,4,6,8302,"霓凰长跪不起，定要皇帝给自己，给穆王府一个交代。",654,39,798,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：40~41\n普通的敌人。"},
key10001163={10001163,10016,3,"抵死不认",1,1,0,10001162,0,1,4,6,8303,"长伴枕边的越贵妃，如何不懂皇帝的软肋，定要歪曲事实死不承认。",655,39,435,"1014001,10;1012001,5;1,110","1,10;2,1200","推荐英雄等级：40~41\n普通的敌人。"},
key11001164={11001164,10016,4,"一声叹息",2,1,0,10001163,0,1,4,6,8304,"靖王本就不受宠，牵扯到太子之事，靖王的话语并无多少分量。",1580,39,1488,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：40~41\nBOSS，擅于内功攻击全体目标。"},
key10001165={10001165,10016,5,"生擒司马雷",1,1,0,11001164,0,1,4,6,8305,"梅长苏先知先行，令慕青守住越贵妃后院，果真擒住了偷溜的司马雷。",652,39,408,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：41~42\n普通的敌人。"},
key11001166={11001166,10016,6,"证据凿凿",1,1,0,10001165,0,1,4,6,8306,"蒙挚将司马雷被擒被揍的消息禀告皇帝，证据凿凿，越贵妃瘫软倒地。",653,39,69,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：41~42\n普通的敌人。"},
key11001168={11001168,10016,7,"保太子",1,1,0,11001166,0,1,4,6,8307,"梁帝震怒，欲重罚太子，越贵妃哭天喊地独自揽下罪责，欲保太子。",657,39,435,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：41~42\n普通的敌人。"},
key10001167={10001167,10016,8,"落下帷幕",3,1,0,11001168,0,1,4,6,8308,"贵妃降位，太子禁闭，靖王无褒无罚，霓凰不甘也无奈，此事于此落幕。",658,39,798,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：41~42\n精英，对随机目标施加持续毒伤害。"},
key10001171={10001171,10017,1,"功不可没",1,1,0,10001167,0,1,4,6,8201,"霓凰深知此次能够全身而退，梅长苏功不可没。",655,41,1488,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：42~43\n普通的敌人。"},
key10001172={10001172,10017,2,"情深难掩",1,1,0,10001171,0,1,4,6,8202,"霓凰约见梅长苏，郑重道谢，从对方眼中读出深情义，霓凰起疑。",654,41,794,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：42~43\n普通的敌人。"},
key10001173={10001173,10017,3,"小殊是你吗",1,1,0,10001172,0,1,4,6,8203,"两人行至原林家府邸，霓凰情绪激动，心中笃定梅长苏定是小殊。",652,41,73,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：42~43\n普通的敌人。"},
key11001174={11001174,10017,4,"终相认",2,1,0,10001173,0,1,4,6,8204,"霓凰泪盈于睫，梅长苏于心不忍终不再隐瞒，承认自己即林殊。",1580,41,1490,"1014001,10;1012001,5;1,110","1,10;2,1220","推荐英雄等级：42~43\n精英，擅于内功攻击单体目标。"},
key10001175={10001175,10017,5,"面目全非",1,1,0,11001174,0,1,4,6,8205,"得知小殊真的回来了，即便面目全非，霓凰心疼之余很是欣喜。",657,41,794,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：43~44\n普通的敌人。"},
key11001176={11001176,10017,6,"缄口不言",1,1,0,10001175,0,1,4,6,8206,"霓凰询问梅岭一役，梅长苏缄口不言，不愿霓凰承担过多。",656,41,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：43~44\n普通的敌人。"},
key11001178={11001178,10017,7,"前路漫漫",1,1,0,11001176,0,1,4,6,8207,"霓凰不愿勉强林殊，得知其未死，已心满意足。",653,41,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：43~44\n普通的敌人。"},
key10001177={10001177,10017,8,"心在靖王",3,1,0,11001178,0,1,4,6,8208,"梅长苏告知霓凰自己并非真的投在誉王门下，提及欲助靖王夺嫡。",658,41,407,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：43~44\nBOSS，擅于内功攻击全体目标。"},
key10001181={10001181,10018,1,"夺嫡的天平",1,1,0,10001177,0,1,4,6,8101,"越贵妃倒台，太子损失了左膀右臂，二龙对峙的天平开始倾斜。",652,43,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key10001182={10001182,10018,2,"妙音坊",1,1,0,10001181,0,1,4,6,8102,"各方势力暗潮汹涌，梅长苏思忖，妙音坊，也需要出手了。",654,43,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key10001183={10001183,10018,3,"宫羽姑娘",1,1,0,10001182,0,1,4,6,8103,"得知将见梅长苏，宫羽心中满是喜悦，盈盈而至。",656,43,794,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key11001184={11001184,10018,4,"君子无心",2,1,0,10001183,0,1,4,6,8104,"梅长苏避开宫羽的好意，仅仅谈及宫中与金陵城各方势力。",1580,43,1488,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n精英，擅于内功攻击全体目标。"},
key10001185={10001185,10018,5,"风花雪月",1,1,0,11001184,0,1,4,6,8105,"宫羽垂睫，心知盟主意不在风花雪月，脑中闪过清明，不再黯然伤神。",657,43,794,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key11001186={11001186,10018,6,"武力重要",1,1,0,10001185,0,1,4,6,8106,"梅长苏提出，妙音坊需要对付秦般弱的红袖招，武力亦十分重要。",655,43,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key11001188={11001188,10018,7,"七窍玲珑心",1,1,0,11001186,0,1,4,6,8107,"宫羽心思玲珑，当即唤出亲卫，多谢盟主不吝赐教。",653,43,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key10001187={10001187,10018,8,"不吝赐教",3,1,0,11001188,0,1,4,6,8108,"梅长苏并未多言，遣人与宫羽亲卫试炼，自己从旁指导。",658,43,797,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：44~45\nBOSS，擅于单体攻击后排单体目标。"},
key10001191={10001191,10019,1,"南楚来客",1,1,0,10001187,0,1,4,6,8401,"表面的金陵十分平静，此时迎来了另一重事，南楚来贵客。",652,44,796,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：45~46\n普通的敌人。"},
key10001192={10001192,10019,2,"宇文兄妹",1,1,0,10001191,0,1,4,6,8402,"南楚皇子宇文暄携妹妹宇文念入金陵，听闻此消息，梅长苏心念一动。",654,44,794,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：45~46\n普通的敌人。"},
key10001193={10001193,10019,3,"登门拜访",1,1,0,10001192,0,1,4,6,8403,"少年郎正是坐不住的年岁，有此盛事，言豫津忙拉萧景睿前往拜访。",653,44,796,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：45~46\n普通的敌人。"},
key11001194={11001194,10019,4,"比武切磋",2,1,0,10001193,0,1,4,6,8404,"鲜衣怒马言豫津，个性最是张扬，听闻宇文暄剑法奇妙，提出切磋。",1580,44,1725,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：45~46\n精英，擅于内功攻击竖列目标。"},
key10001195={10001195,10019,5,"宇文念",1,1,0,11001194,0,1,4,6,8405,"哪曾想，宇文暄尚未出手，其妹宇文念点名要与萧景睿切磋一二。",656,44,795,"1014001,10;1012001,5;1,110","1,10;2,1240","推荐英雄等级：46~47\n普通的敌人。"},
key11001196={11001196,10019,6,"点到即止",1,1,0,10001195,0,1,4,6,8406,"景睿性子沉稳，耐不住言豫津热烈怂恿，答应点到即止。",657,44,794,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：46~47\n普通的敌人。"},
key11001198={11001198,10019,7,"胜负难辨",1,1,0,11001196,0,1,4,6,8407,"二人宝剑出鞘影随身至，片刻便斗在一起，胜负难辨。",655,44,795,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：46~47\n普通的敌人。"},
key10001197={10001197,10019,8,"景睿小胜",3,1,0,11001198,0,1,4,6,8408,"宇文念行动渐慢，出招渐渐吃力，梅长苏喝止二人以免受伤。",658,44,1487,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：46~47\nBOSS，擅于内功攻击竖列目标。"},
key10001201={10001201,10020,1,"见好就收",1,1,0,10001197,0,1,4,6,8301,"宇文念萧景睿双双收手，景睿拱手客气，夸赞宇文姑娘剑法。",656,46,423,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：47~48\n普通的敌人。"},
key10001202={10001202,10020,2,"不怕输",1,1,0,10001201,0,1,4,6,8302,"宇文念有样学样，客气几句，小小年纪倒是十分大气。",654,46,67,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：47~48\n普通的敌人。"},
key10001203={10001203,10020,3,"心疼妹妹",1,1,0,10001202,0,1,4,6,8303,"妹妹落败，哥哥宇文暄看在眼中，心中却生出不满。",655,46,434,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：47~48\n普通的敌人。"},
key10001204={10001204,10020,4,"宇文暄",2,1,0,10001203,0,1,4,6,8304,"宇文暄提剑相邀，希望与梅长苏比试一番，倒惹急了景睿豫津。",1580,46,1489,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：47~48\n精英，擅于内功攻击全体目标。"},
key11001205={11001205,10020,5,"不会武",1,1,0,10001204,0,1,4,6,8305,"言豫津快人快语，提出梅长苏不会武功，宇文暄心中不信却并不相逼。",652,46,902,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：48~49\n普通的敌人。"},
key11001206={11001206,10020,6,"景睿代替",1,1,0,11001205,0,1,4,6,8306,"梅长苏坦然一笑，以身体不佳为由，提出口述技法，由景睿替自己打。",657,46,423,"1014001,10;1012001,5;1,110","1,10;2,1260","推荐英雄等级：48~49\n普通的敌人。"},
key11001207={11001207,10020,7,"江左盟主",1,1,0,11001206,0,1,4,6,8307,"宇文暄痛快应下，与景睿双双提剑，梅长苏并未藏拙，用心指点。",653,46,902,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：48~49\n普通的敌人。"},
key10001208={10001208,10020,8,"精妙之人",3,1,0,11001207,0,1,4,6,8308,"江左梅郎并非虚名，梅长苏言简意赅句句精妙，双发打得十分尽兴。",658,46,434,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：48~49\nBOSS，擅于内功攻击单体目标。"},
key10001211={10001211,10021,1,"心服口服",1,1,0,10001208,0,1,4,6,8101,"宇文暄终是败下阵来，知道自己技不如人心服口服，多谢对方赐教。",653,48,434,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001212={10001212,10021,2,"风起潮涌",1,1,0,10001211,0,1,4,6,8102,"青年男女尽兴而归，另一面金陵城却发生了一件耸人惊闻的案子。",652,48,797,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001213={10001213,10021,3,"大年夜",1,1,0,10001212,0,1,4,6,8103,"大年三十，梁帝亲赐重臣勋贵年夜食盒以示嘉奖，却喜事变命案。",655,48,797,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001214={10001214,10021,4,"凶杀案",2,1,0,10001213,0,1,4,6,8104,"前往各府赐菜的内侍，在静谧的年夜街道，俱被刺杀殒命。",1580,48,420,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n精英，擅于内功攻击全体目标。"},
key11001215={11001215,10021,5,"蒙挚被责",1,1,0,10001214,0,1,4,6,8105,"此事等同于直接打在梁帝脸上，帝王大怒，首当其责便是蒙挚。",654,48,69,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key11001216={11001216,10021,6,"诡异的案子",1,1,0,11001215,0,1,4,6,8106,"蒙挚掌管金陵巡防，金陵保卫出事，还是如此诡异凶杀案，十分头大。",657,48,69,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key11001217={11001217,10021,7,"十日之期",1,1,0,11001216,0,1,4,6,8107,"梁帝定下十日破案，蒙挚心焦寻访梅长苏，拜托其帮忙找寻凶手。",656,48,902,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001218={10001218,10021,8,"并不简单",3,1,0,11001217,0,1,4,6,8108,"梅长苏看破此事定然不是刺杀如此简单，派出手下细细查访。",658,48,481,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：49~50\nBOSS，擅于内功攻击竖列目标。"},
key10001221={10001221,10022,1,"世间因果",1,1,0,10001218,0,1,4,6,8401,"这世间的事情，无论抹除得多干净，都不能代表事情没有发生过。",655,49,774,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：50~51\n普通的敌人。"},
key10001222={10001222,10022,2,"蛛丝马迹",1,1,0,10001221,0,1,4,6,8402,"发生的事情，就一定有蛛丝马迹。事件的果，必有出事的因。",657,49,774,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：50~51\n普通的敌人。"},
key10001223={10001223,10022,3,"了无痕迹",1,1,0,10001222,0,1,4,6,8403,"反倒因为了无痕迹，便露出马脚。能做到如此之人，世上有几个？",656,49,774,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：50~51\n普通的敌人。"},
key10001224={10001224,10022,4,"心知肚明",2,1,0,10001223,0,1,4,6,8404,"梅长苏心中其实已有答案，然而案子需要证据，巡防营依旧忙碌找寻。",1580,49,756,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：50~51\n精英，擅于内功攻击竖列目标。"},
key10001225={10001225,10022,5,"引蛇出洞",1,1,0,10001224,0,1,4,6,8405,"梅长苏让蒙挚放出假风声，巡防营已经找到重要证据，引蛇出洞。",653,49,69,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：51~52\n普通的敌人。"},
key11001226={11001226,10022,6,"神秘高手",1,1,0,10001225,0,1,4,6,8406,"果不其然出现神秘的高手阻碍查证，剑法诡秘，侍卫们均不是对手。",652,49,438,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：51~52\n普通的敌人。"},
key11001227={11001227,10022,7,"自乱阵脚",1,1,0,11001226,0,1,4,6,8407,"梅长苏心底冷笑，小小计谋，对方已经如此按耐不住自乱阵脚。",654,49,902,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：51~52\n普通的敌人。"},
key10001228={10001228,10022,8,"正合心意",3,1,0,11001227,0,1,4,6,8408,"看来对方，比自己还要心急啊，神秘高手的出现正合心意。",658,49,438,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：51~52\nBOSS，擅于内功攻击全体目标。"},
key10001231={10001231,10023,1,"请君入瓮",1,1,0,10001228,0,1,4,6,8201,"梅长苏蒙挚早已布下埋伏，一切意在请君入瓮，果然擒住了神秘高手。",653,51,69,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：52~53\n普通的敌人。"},
key10001232={10001232,10023,2,"真面目",1,1,0,10001231,0,1,4,6,8202,"黑衣人目露惊恐，蒙挚上前道：“待我除你面纱，看看你的真面目！”",652,51,69,"1014001,10;1012001,5;1,110","1,10;2,1280","推荐英雄等级：52~53\n普通的敌人。"},
key10001233={10001233,10023,3,"卓青遥",1,1,0,10001232,0,1,4,6,8203,"面纱掀开，现于人前的竟是天泉山庄卓青遥，景睿的卓家哥哥兼妹夫。",657,51,438,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：52~53\n普通的敌人。"},
key10001234={10001234,10023,4,"天泉山庄",2,1,0,10001233,0,1,4,6,8204,"蒙挚十分震惊，江湖人不管朝堂事，不懂为何卓家参与这宫闱凶案。",1580,51,438,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：52~53\n精英，擅于内功攻击全体目标。"},
key10001235={10001235,10023,5,"可怜人",1,1,0,10001234,0,1,4,6,8205,"梅长苏面色如常，似是早已知情，望着桌青遥的眼神不知觉带上怜悯。",654,51,902,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：53~54\n普通的敌人。"},
key11001236={11001236,10023,6,"江左暗卫",1,1,0,10001235,0,1,4,6,8206,"插手调查的是江湖人士，梅长苏看出蒙挚的为难，吟笛唤出江左暗卫。",656,51,1773,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：53~54\n普通的敌人。"},
key11001237={11001237,10023,7,"飞影至",1,1,0,11001236,0,1,4,6,8207,"江左盟，即便卓泉山庄也畏惧十分，瞬间，一道飞影掳走了卓青遥。",655,51,438,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：53~54\n普通的敌人。"},
key10001238={10001238,10023,8,"卓鼎风",3,1,0,11001237,0,1,4,6,8208,"蒙挚欲追，梅长苏将其拉住，如此身手，只有卓泉山庄站住卓鼎风了。",658,51,437,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：53~54\nBOSS，擅于内功攻击竖列目标。"},
key10001241={10001241,10024,1,"并非所盼",1,1,0,10001238,0,1,4,6,8101,"梅长苏等的，就是卓鼎风有没有参与，等来了该来的，心却异常堵闷。",654,53,902,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001242={10001242,10024,2,"景睿的生辰",1,1,0,10001241,0,1,4,6,8102,"无论发生了什么事情，金陵城该行的轨迹并无变化，比如景睿的生辰。",656,53,423,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001243={10001243,10024,3,"贵公子",1,1,0,10001242,0,1,4,6,8103,"景睿同是谢家与卓家公子，更是冠天家姓氏的贵人，生辰规模盛大。",653,53,423,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001244={10001244,10024,4,"门庭若市",2,1,0,10001243,0,1,4,6,8104,"除却身份不说，景睿交友一向亲善，人缘甚好，一时间谢府门庭若市。",1580,53,1726,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n精英，擅于内功攻击单体目标。"},
key10001245={10001245,10024,5,"不速之客",1,1,0,10001244,0,1,4,6,8105,"景睿今日一身儒贵，举手投足风度翩翩，此时却迎来一位不速之客。",657,53,423,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key11001246={11001246,10024,6,"掌镜史夏冬",1,1,0,10001245,0,1,4,6,8106,"悬镜司掌镜史夏冬携剑登门，众人避让，一时之间猜测纷纷。",655,53,419,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key11001247={11001247,10024,7,"刺杀案",1,1,0,11001246,0,1,4,6,8107,"两人私交甚好，景睿不明夏冬来意，夏冬抬手安抚，行至梅长苏身前。",653,53,902,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001248={10001248,10024,8,"江湖中人",3,1,0,11001247,0,1,4,6,8108,"就查案来说，夏冬与梅长苏关注的都是一个点，必是江湖高手下的手。",658,53,419,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：54~55\nBOSS，擅于内功攻击竖列目标。"},
key10001251={10001251,10025,1,"静候真相",1,1,0,10001248,0,1,4,6,8201,"夏冬怀疑江左盟与刺杀案逃不开关系，梅长苏却一句话便稳住了夏冬。",656,54,419,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：55~56\n普通的敌人。"},
key10001252={10001252,10025,2,"热闹纷呈",1,1,0,10001251,0,1,4,6,8202,"夏冬收起兵器，只当来参加生日宴，众人逐渐放开，宴会热闹起来。",655,54,419,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：55~56\n普通的敌人。"},
key10001253={10001253,10025,3,"南楚公子",1,1,0,10001252,0,1,4,6,8203,"说话间迎来了几位特殊的客人，南楚宇文暄兄妹和一位公子登门拜访。",652,54,67,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：55~56\n普通的敌人。"},
key10001254={10001254,10025,4,"岳秀泽",2,1,0,10001253,0,1,4,6,8204,"来人原是琅琊高手榜排行第五的剑客--岳秀泽，意在以武会友。",1580,54,67,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：55~56\n精英，擅于内功攻击竖列目标。"},
key10001255={10001255,10025,5,"卓家剑法",1,1,0,10001254,0,1,4,6,8205,"卓家剑法闻名天下，岳秀择诚意相邀，众目睽睽卓鼎风无法回绝。",653,54,437,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：56~57\n普通的敌人。"},
key11001256={11001256,10025,6,"高手过招",1,1,0,10001255,0,1,4,6,8206,"卓鼎风应下比试，二人转眼之间便斗在一起，剑影翩翩，眼花缭乱。",657,54,1115,"1014001,10;1012001,5;1,110","1,10;2,1300","推荐英雄等级：56~57\n普通的敌人。"},
key11001257={11001257,10025,7,"怀疑的种子",1,1,0,11001256,0,1,4,6,8207,"望着二人缠斗的身影，蒙挚夏冬微微眯眼，心中怀疑大盛。",652,54,419,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：56~57\n普通的敌人。"},
key10001258={10001258,10025,8,"心中之苦",3,1,0,11001257,0,1,4,6,8208,"回望蒙挚和夏冬，卓鼎风心中苦笑，若出全力，便要暴露刺客身份。",658,54,437,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：56~57\nBOSS，擅于内功攻击全体目标。"},
key10001261={10001261,10026,1,"毅然绝然",1,1,0,10001258,0,1,4,6,8301,"剑至身前，卓鼎风心中已有决意，动作带出一股决然之气。",656,56,437,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：57~58\n普通的敌人。"},
key10001262={10001262,10026,2,"唯快不破",1,1,0,10001261,0,1,4,6,8302,"天下武功唯快不破，眨眼间众人便看到一片血雾飞起，两人停下动作。",655,56,1115,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：57~58\n普通的敌人。"},
key10001263={10001263,10026,3,"壮士断腕",1,1,0,10001262,0,1,4,6,8303,"待人看清，一阵惊呼。卓鼎风一腕断裂血如泉涌，岳秀泽面露震惊。",657,56,437,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：57~58\n普通的敌人。"},
key10001264={10001264,10026,4,"已有答案",2,1,0,10001263,0,1,4,6,8304,"卓鼎风稳住伤势，抬手认败。夏冬与蒙挚互望一刹，心中已有答案。",1580,56,1725,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：57~58\n精英，擅于内功攻击最前排目标。"},
key10001265={10001265,10026,5,"线索断",1,1,0,10001264,0,1,4,6,8305,"卓鼎风的壮士断腕，将刺杀案最后的线索一并断掉。",652,56,1115,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：58~59\n普通的敌人。"},
key11001266={11001266,10026,6,"妙音坊宫羽",1,1,0,10001265,0,1,4,6,8306,"比武结束，宴会继续，妙音坊宫羽姑娘盈盈而至，抚琴之音犹如天籁。",654,56,401,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：58~59\n普通的敌人。"},
key11001267={11001267,10026,7,"杀手相思",1,1,0,11001266,0,1,4,6,8307,"琴声戛然而止，宫羽突然道出自己凄苦身世，生父竟是消失的杀手相思。",653,56,1113,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：58~59\n普通的敌人。"},
key10001268={10001268,10026,8,"来不及了",3,1,0,11001267,0,1,4,6,8308,"宫羽道出当年父亲受雇于谢玉，杀害谢卓两家新生婴儿。",658,56,1113,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：58~59\nBOSS，擅于外功攻击单体目标。"},
key10001271={10001271,10027,1,"当年真相",1,1,0,10001268,0,1,4,6,8401,"然而相思只来得及杀害了其中一个孩子，便是卓家的孩儿。",652,58,437,"1014001,10;1012001,5;1,110","1,10;2,1320","推荐英雄等级：59~60\n普通的敌人。"},
key10001272={10001272,10027,2,"恼羞成怒",1,1,0,10001271,0,1,4,6,8402,"宫羽黯然垂泪，父亲因为此事，终日被谢玉追杀，终是死于非命。",656,58,1113,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key10001273={10001273,10027,3,"谢玉的阴谋",1,1,0,10001272,0,1,4,6,8403,"谢玉眼看不妙，欲阻止宫羽，被大惊的卓鼎风制止，再欲出手已晚。",653,58,424,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key10001274={10001274,10027,4,"面对质问",2,1,0,10001273,0,1,4,6,8404,"宫羽冷笑，指出若是不信，大可去问莅阳长公主的老嬷嬷。",1580,58,1726,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\n精英，擅于外功攻击单体目标。"},
key10001275={10001275,10027,5,"杀人灭口",1,1,0,10001274,0,1,4,6,8405,"卓鼎风眼看恼羞成怒的谢玉，面色凄然的莅阳长公主，终于相信。",657,58,437,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key11001276={11001276,10027,6,"卓家的孩儿",1,1,0,10001275,0,1,4,6,8406,"原来谢玉不仅指示杀手杀害了自家孩儿，还利用天泉山庄这么多年。",654,58,437,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key11001277={11001277,10027,7,"质问谢玉",1,1,0,11001276,0,1,4,6,8407,"卓鼎风怒急攻心，莅阳长公主亦是泪流满面忿恨的质问谢玉。",655,58,400,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key10001278={10001278,10027,8,"悔不当初",3,1,0,11001277,0,1,4,6,8408,"宾客哗然，卓鼎风得知真相，亦锥心后悔这些年为谢玉做的歹事。",658,58,424,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：59~60\nBOSS，擅于内功攻击单体目标。"},
key10001281={10001281,10028,1,"召来私兵",1,1,0,10001278,0,1,4,6,8201,"一场生日宴演变成金陵秘闻，谢玉大骇，召来巡防营欲杀人灭口。",652,59,424,"1014001,10;1012001,5;1,110","1,10;2,1340","推荐英雄等级：60\n普通的敌人。"},
key11001282={11001282,10028,2,"身败名裂",1,1,0,10001281,0,1,4,6,8202,"在场皆是权贵，今日之事一旦泄露，谢玉心知自己必将身败名裂。",653,59,424,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\n普通的敌人。"},
key10001283={10001283,10028,3,"冰山一角",1,1,0,11001282,0,1,4,6,8203,"宫羽说出的，只是冰山一角，之后引出的事情，谢玉不敢想象。",654,59,401,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\n普通的敌人。"},
key10001284={10001284,10028,4,"都得死！",2,1,0,10001283,0,1,4,6,8204,"听了不该听的人，都得死！谢玉目赤，已然丧失理智。",1580,59,1725,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\n精英，擅于内功攻击最前排目标。"},
key10001285={10001285,10028,5,"关门杀人",1,1,0,10001284,0,1,4,6,8205,"谢玉大手一挥，谢家大门嘎然关闭，宾客顿时乱作一团。",655,59,424,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\n普通的敌人。"},
key11001286={11001286,10028,6,"血洗谢府",1,1,0,10001285,0,1,4,6,8206,"谢玉召来亲兵士卫，意图血洗谢府，来个死无对证。",656,59,424,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\n普通的敌人。"},
key11001287={11001287,10028,7,"一夜成人",1,1,0,11001286,0,1,4,6,8207,"眼看着疯狂的父亲，景睿面如金纸，扶着莅阳长公主，似是一夜成人。",657,59,423,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\n普通的敌人。"},
key10001288={10001288,10028,8,"走下去",3,1,0,11001287,0,1,4,6,8208,"望着不再温润的景睿，梅长苏眼中闪过不忍，然而这条路，必须走下去。",658,59,424,"1014001,10;1012001,5;1,110","1,10;2,1380","推荐英雄等级：60\nBOSS，擅于内功攻击单体目标。"},
key10001291={10001291,10029,1,"云初",1,1,0,90009009,0,1,4,6,8101,"鲜花满月水长留， 花满心时亦满楼",657,40,774,"","1,10;2,1400","简单的怪物，很容易通关"},
key10001292={10001292,10029,2,"青城剑",1,1,0,10001291,0,1,4,6,8102,"看舞霓裳羽衣曲，听歌玉树后庭花",652,40,1489,"","1,10;2,1400","简单的怪物，很容易通关"},
key10001293={10001293,10029,3,"倾城颜",2,1,0,10001292,0,1,4,6,8103,"痴心一曲己不知，花妖韵犹千千知",1580,40,412,"","1,10;2,1420","简单的怪物，很容易通关"},
key10001294={10001294,10029,4,"清城举",1,1,0,10001293,0,1,4,6,8104,"十虎争天不图巧，拳扯天地勿醉聪",653,40,792,"","1,10;2,1420","简单的怪物，很容易通关"},
